mapper.select:
select *
from reple where user_id = #{user_id}, reple_contents =
${reple_contents}

select
reple_good from reple
where reple_id = #{reple_id}

mapper.update:
update reple set
REPLE_REMOVE_LEV = 1
where reple_id = #{reple_id}

mapper.delete:
delete from user_good
where user_id = #{user_id} and reple_id = #{reple_id} and play_id =
#{play_id}

mapper.update:
update reple set
reple_good = reple_good + 1
where reple_id = #{reple_id}

update reple set
reple_good = reple_good - 1
where reple_id = #{reple_id}
